import React, { useState, useEffect } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  Navigate,
} from "react-router-dom";
import { immeOauth, immeLogout } from "cycurid-widget-js";
import { immeVerification } from "cycurid-verification-js";
import Login from "./components/login.component";
import SignUp from "./components/signup.component";
import Success from "./components/success.component";

function App() {
  const [username, setUsername] = useState();
  const [userData, setUserData] = useState();
  const [load, setLoad] = useState();
  const [token, setToken] = useState();
  const config = {
    origin_url: process.env.REACT_APP_ORIGIN_URL,
    client_secret: process.env.REACT_APP_CLIENT_SECRET,
    client_id: process.env.REACT_APP_CLIENT_ID,
    scope: [
      "reference_uuid",
      // "email",
      // "phone",
      "first_name",
      // "last_name",
      // "middle_name",
      // "dob",
      // "address",
      // "city",
      // "zip",
    ],
    entity_name: "Imme Test Entity",
  };

  const myUserData = {
    verification: {
      //callback: "https://e83c-209-121-124-51.ngrok.io",
      callback: "https://google.ca",
      person: {
        first_name: "Jordan",
        last_name: "MEHRTASH",
      },
      documents: {
        type: "driver_license",
        //driver_license
        //passport
        number: "HP458183",
        country: "can",
      },
      internal_reference: "dsafsdfasdfasdfasdf",
    },
  };

  const verificationConfig = {
    client_api_key: process.env.REACT_APP_IMME_API_KEY,
    client_api_secret: process.env.REACT_APP_IMME_API_SECRET,
    verifiable_data: myUserData,
  };

  function onSuccess(data, token) {
    setUsername(data.reference_uuid);
    setUserData(data.user_data);
    setToken(token);
    console.log("Data requested from scope: ", data);
  }

  function onFailure(data) {
    console.log(data);
  }

  async function signIn(e) {
    e.preventDefault();
    immeOauth({ ...config, action: "login" }, onSuccess, onFailure);
  }
  async function signUp(e) {
    e.preventDefault();
    immeOauth({ ...config, action: "login" }, onSuccess, onFailure);
  }
  async function verify(e) {
    e.preventDefault();
    immeVerification(verificationConfig, onSuccess, onFailure);
  }
  function logout() {
    immeLogout(token.access_token, config.client_id, config.client_secret);
    setUsername(null);
  }

  useEffect(() => {
    setLoad(false);
  }, [load]);
  return (
    <Router>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light fixed-top">
          <div className="container">
            <Link className="navbar-brand" to={"/sign-in"}>
              CycurID
            </Link>
            {!username && (
              <div
                className="collapse navbar-collapse"
                id="navbarTogglerDemo02"
              >
                <ul className="navbar-nav ml-auto">
                  <li className="nav-item">
                    <Link className="nav-link" to={"/sign-in"}>
                      Login
                    </Link>
                  </li>
                  <li className="nav-item">
                    <Link className="nav-link" to={"/sign-up"}>
                      Sign up
                    </Link>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </nav>

        <div className="auth-wrapper">
          <div className="auth-inner">
            <Routes>
              <Route
                exact
                path="/"
                element={
                  !username ? (
                    <Login
                      signIn={signIn}
                      verify={verify}
                      setUsername={setUsername}
                      setToken={setToken}
                    />
                  ) : (
                    <Navigate replace to="/success" />
                  )
                }
              />
              <Route
                path="/sign-in"
                element={
                  !username ? (
                    <Login signIn={signIn} verify={verify} />
                  ) : (
                    <Navigate replace to="/success" />
                  )
                }
              />
              <Route
                path="/sign-up"
                element={
                  !username ? (
                    <SignUp signUp={signUp} />
                  ) : (
                    <Navigate replace to="/success" />
                  )
                }
              />

              <Route
                path="/success"
                element={
                  username ? (
                    <Success logout={() => logout} userData={userData} />
                  ) : (
                    <Navigate replace to="/" />
                  )
                }
              />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
